CREATE TABLE trampa (
    id INT PRIMARY KEY,
	nombre VARCHAR(255),
    tipo_trampa VARCHAR(255),
    efecto VARCHAR(10000),
	imagen VARCHAR(255),
    FOREIGN KEY (id) REFERENCES carta(id)
);

