CREATE TABLE usuario (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nick VARCHAR(255) UNIQUE,
    correo VARCHAR(255) UNIQUE,
    contrasenya VARCHAR(255),
    imagen VARCHAR(255)
);

