CREATE TABLE carta_deck (
    id_carta INT,
    id_deck INT,
    PRIMARY KEY (id_carta, id_deck),
    FOREIGN KEY (id_carta) REFERENCES carta(id),
    FOREIGN KEY (id_deck) REFERENCES deck(id)
);

